sys_variable_value.value:
(function execute(inputs, outputs) {
  let message = '<ul>';
  const sims = inputs.simRecords;

  while (sims.next()) {
    message += `<li>${sims.full_name.getDisplayValue()} (${sims.species.getDisplayValue()} - ${sims.age.getDisplayValue()})</li>`;
  }
  message += '</ul>'
  outputs.outmessage = message
})(inputs, outputs);

